# http://www.pythonchallenge.com/pc/def/equality.htmlfrom curses.ascii import islower, isupperf =  open("characters")def createCharacterMatrix(f):    characters = []    L1 = []    for line in f:        characters.append(list(line))    return charactersdef checkSmallBetween3Big(li,co):    ret = \        islower(charMatrix[li - 4][co]) and \        isupper(charMatrix[li - 3 ][co]) and \        isupper(charMatrix[li - 2][co]) and \        isupper(charMatrix[li - 1][co]) and \        islower(charMatrix[li][co - 4]) and \        isupper(charMatrix[li][co - 3 ]) and \        isupper(charMatrix[li][co - 2]) and \        isupper(charMatrix[li][co - 1]) and \        islower(charMatrix[li][co]) and \        isupper(charMatrix[li][co + 1]) and \        isupper(charMatrix[li][co + 2]) and \        isupper(charMatrix[li][co + 3]) and \        islower(charMatrix[li][co + 4]) and \        isupper(charMatrix[li + 1][co]) and \        isupper(charMatrix[li + 2][co]) and \        isupper(charMatrix[li + 3][co]) and \        islower(charMatrix[li + 4][co])    if ret:        print(charMatrix[li - 4][co])        print(charMatrix[li - 3][co])        print(charMatrix[li - 2][co])        print(charMatrix[li - 1][co])        print(charMatrix[li][co - 4], end='')        print(charMatrix[li][co - 3],end='')        print(charMatrix[li][co - 2],end='')        print(charMatrix[li][co - 1],end='')        print(charMatrix[li][ co],end='')        print(charMatrix[li][co + 1],end='')        print(charMatrix[li][co + 2],end='')        print(charMatrix[li][co + 3], end='')        print(charMatrix[li][co + 4])        print(charMatrix[li + 1][co])        print(charMatrix[li + 2][co])        print(charMatrix[li + 3][co])        print(charMatrix[li + 4][co])    return retcharMatrix = createCharacterMatrix(f)lines = len(charMatrix)cols = len(charMatrix[0])for l in range(4,lines-4):    for c in range(4,cols-5):        if checkSmallBetween3Big(l,c):            print(charMatrix[l][c],end = '')